success=0; failure=0; for i in $(seq 100); do [ $(curl -s -o /dev/null -w "%{http_code}" -H "X-Return-Encrypted-Headers: request_and_response" https://test-deployment.tupperware.eu/ --connect-timeout 60) -ge 200 ] && success=$((success+1)) || failure=$((failure+1)); done; echo "Success: $success, Failure: $failure, Success Rate: $((success * 100 / 100))%"
## With results show:

success=0; failure=0; success_req=""; timeout_req=""; for i in $(seq 10); do result=$(curl -s -w "%{http_code}" -o /dev/null -H "X-Return-Encrypted-Headers: request_and_response" https://test-deployment.tupperware.eu/ --connect-timeout 15); if [[ $result -ge 200 && $result -lt 300 ]]; then success=$((success+1)); [ -z "$success_req" ] && success_req=$(curl -s -H "X-Return-Encrypted-Headers: request_and_response" https://test-deployment.tupperware.eu/); else failure=$((failure+1)); [ "$result" == "000" ] && [ -z "$timeout_req" ] && timeout_req="Timeout occurred at request $i"; fi; done; echo "Success: $success, Failure: $failure, Success Rate: $((success * 100 / (success + failure)))%"; echo "Example Success Response: $success_req"; echo "Example Timeout: $timeout_req"
